import React, { Component } from "react";

class User extends Component {
  state = {
    formData: {
      name: {
        element: "input",
        value: "",
        label: true,
        labelText: "Name",
        config: {
          name: "name_input",
          text: "text",
          placeholder: "Enter your name"
        }
      }
    }
  };

  render() {
    return (
      <div className="container">
        <form onSubmit={this.submitForm}>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default User;
