import React, { Component } from "react";
class Form extends Component {
  handleChange = ({ currentTarget: input }) => {
    const data = { ...this.state.data };
    data[input.name] = input.value;
    this.setState({ data });
  };
  handleSubmit = e => {
    e.preventDefault();
    doOnSubmit();
  };
}

export default Form;
