import React, { Component } from "react";
import Logo from "../../images/logo.png";
import { Link, Route, BrowserRouter as Router } from "react-router-dom";

import WebSite from "../_webSite/WebSite";
import WebSiteTasks from "../WebSiteTasks/WebSiteTasks";
import WebSiteProjects from "../WebSiteProjects/WebSiteProjects";
import App from "../../App";
import loginForm from "../_forms/loginForm";
import Body from "./Body";

export default class Header extends Component {
  constructor() {
    super();
    this.state = {
      route: [
        {
          isClicked: false,
          path: "/webSite",
          component: WebSite,
          navBar: {
            webSiteInfo: "Web Site Info",
            credentials: "Credentials",
            status: "Status"
          }
        },
        {
          isClicked: false,
          path: "/task",
          component: WebSiteTasks
        }
      ]
    };
  }
  onClickHandler = linkName => {

    this.state.route.map(route => {

      if (route.path === linkName) {
        console.log(route.path);
        this.setState({
          isClicked: true
        });
      }
      console.log(this.state.route);
    });
  };

  render() {
    return (
      <React.Fragment>
        <Router>
          <nav className="navbar navbar-inverse">
            <div className="container-fluid">
              <div className="navbar-header">
                <button
                  type="button"
                  className="navbar-toggle"
                  data-toggle="collapse"
                  data-target="#myNavbar"
                >
                  <span className="icon-bar" />
                  <span className="icon-bar" />
                  <span className="icon-bar" />
                </button>
                <img className="navbar-brand" src={Logo} alt="webiste logo" />
              </div>
              <div className="collapse navbar-collapse" id="myNavbar">
                <ul className="nav navbar-nav">
                  <li className="active">
                    <Link to="/">Home</Link>
                  </li>
                  <li>
                    <Link
                      to="/website"
                      onClick={() => this.onClickHandler("/webSite")}
                    >
                      Web Site
                    </Link>
                  </li>
                  <li>
                    <Link to="/tasks">Tasks</Link>
                  </li>
                  <li>
                    <Link to="/projects">Projects</Link>
                  </li>
                </ul>
                <ul className="nav navbar-nav navbar-right">
                  <li>
                    <Link to="/login">
                      <span className="glyphicon glyphicon-log-in" /> Login
                    </Link>
                  </li>
                </ul>
              </div>
            </div>
          </nav>

          <Route path="/login" component={loginForm} />
          <Route exact path="/" component={App} />
          <Route
            path="/webSite"
            render={props => <Body routeState={this.state.route} {...props} />}
          />
          <Route path="/tasks" component={WebSiteTasks} />
          <Route path="/projects" component={WebSiteProjects} />
        </Router>
      </React.Fragment>
    );
  }
}
