import React, { Component } from "react";
import DatePicker from "react-datepicker";
import moment from "moment";

import "react-datepicker/dist/react-datepicker.css";

export default class PopUpModalContent extends Component {
  render() {
    return (
      <div className="modalContent">
        <h1>Edit content</h1>
        <form>
          <div class="form-group">
            <label>Id</label>
            <input
              type="text"
              class="form-control"
              value={this.props.state.object.id}
            />
          </div>
          <div class="form-group">
            <DatePicker
              dateFormat="yyyy-MM-dd"
              selected={moment(
                moment(this.state.object.date).format("DD-MM-YYYY")
              ).toDate()}
              onChange={this.props.dateChange}
            />
          </div>

          <div class="form-group">
            <label>Activity</label>
            <input
              type="text"
              class="form-control"
              value={this.props.state.object.activity}
              onChange={this.props.dateChange}
            />
          </div>
          <div class="form-group">
            <label>Description</label>
            <input
              type="text"
              class="form-control"
              value={this.props.state.object.description}
              onChange={this.props.handleInputChange}
            />
          </div>
          <button
            type="submit"
            class="btn btn-outline-primary"
            onClick={this.props.onHandleSubmit}
          >
            Save
          </button>
          <button
            type="cancel"
            class="btn btn-outline-secondary"
            onClick={this.props.goBack}
          >
            Cancel
          </button>
        </form>
      </div>
    );
  }
}
