import React, { Component } from "react";

import TableBody from "./common/status_table_body";
import Modal from "react-awesome-modal";
import PopUpModalContent from "./common/PopUpModalContent";

export default class Status extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visible: false,
      object: {}
    };
  }

  openModal = () => {
    this.setState({
      visible: true
    });
  };

  closeModal = () => {
    this.setState({
      visible: false
    });
  };

  goBack = () => {
    window.history.back();
  };

  handleEditIdClick = item => {
    this.setState({
      object: item
    });
  };

  handleChange = event => {
    this.setState({
      object: event.target.value
    });
  };
  render() {
    return (
      <div>
        <div className="status-table">
          <button className="btn btn-primary" onClick={this.openNewModal}>
            Add new
          </button>
          <table class="table">
            <thead class="thead-dark">
              <tr>
                <th scope="col">#</th>
                <th scope="col">Date</th>
                <th scope="col">Activity</th>
                <th scope="col">Description</th>
                <th scope="col">Actions</th>
              </tr>
            </thead>
            <TableBody
              openModal={this.openModal}
              closeModal={this.closeModal}
              goBack={this.goBack}
              editIdState={this.state.editId}
              handleEditIdClick={this.handleEditIdClick}
            />
          </table>
          <div>
            <Modal
              visible={this.state.visible}
              width="900"
              height="700"
              effect="fadeInUp"
              onClickAway={() => this.closeModal}
            >
              <PopUpModalContent
                state={this.state}
                handleInputChange={this.handleChange}
              />
            </Modal>
          </div>
        </div>
      </div>
    );
  }
}
