import React, { Component } from "react";
import Header from "./_components/_navBar/Header";
import Body from "./_components/_navBar/Body";
import Footer from "./_components/_navBar/Footer";
import LoginForm from "./_components/_forms/loginForm";

class App extends Component {
  constructor() {
    super();
    this.state = {
      route: {
        webSite: {
          isClicked: false,
          path: "/webSite",
          component: WebSite
        },
        tasks: {
          isClicked: false,
          path: "/task",
          component: WebSiteTasks
        }
      }
    };
  }

  onClickHandler = linkName => {
    this.state.route.map(() => {
      if (route.path === linkName) {
        this.setState({
          isClicked: true
        });
      }
    });
  };

  render() {
    return (
      <React.Fragment>
        <Header />
        <Body routeState={this.state.route} />
        <Footer />
      </React.Fragment>
    );
  }
}
export default App;
