import React, { Component } from "react";
import Header from "./_components/_navBar/Header";
import Body from "./_components/_navBar/Body";
import Footer from "./_components/_navBar/Footer";
import LoginForm from "./_components/_forms/loginForm";
import WebSite from "./_components/_webSite/WebSite";
import WebSiteTasks from "./_components/WebSiteTasks/WebSiteTasks";

class App extends Component {


  onClickHandler = (evt, linkName) => {
    evt.preventDefault();
    console.log(linkName);
    this.state.route.map(route => {
      console.log(route);
      if (route.path === linkName) {
        console.log(route.path);
        this.setState({
          isClicked: true
        });
      }
      console.log(this.state.route);
    });
  };

  render() {
    return (
      <React.Fragment>
        <Header onClickHandler={this.onClickHandler} />
        <Body routeState={this.state.route} />
        <Footer />
      </React.Fragment>
    );
  }
}
export default App;
